import React, { useState } from 'react';
import { addCar } from '../api';

const AddCar = () => {
  const [carData, setCarData] = useState({
    brand: '',
    vehicleType: '',
    fuelType: '',
    capacity: '',
    pricePerDay: '',
  });

  const handleChange = e => {
    setCarData({...carData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      // Prepare the payload - convert capacity and pricePerDay to numbers
      const payload = {
        ...carData,
        capacity: Number(carData.capacity),
        pricePerDay: Number(carData.pricePerDay),
        status: 'available'  // or any default status your Car class requires
      };

      await addCar(payload);
      alert('Car added successfully');
      // reset form
      setCarData({
        brand: '',
        vehicleType: '',
        fuelType: '',
        capacity: '',
        pricePerDay: '',
      });
    } catch (err) {
      alert('Failed to add car: ' + (err.response?.data || err.message));
    }
  };

  return (
    <form onSubmit={handleSubmit} className="card p-4 mb-4 shadow-sm">
  <h3 className="mb-3">Add New Car</h3>
  <div className="row">
    <div className="col">
      <input className="form-control mb-2" value={brand} onChange={e => setBrand(e.target.value)} placeholder="Brand" required />
    </div>
    <div className="col">
      <input className="form-control mb-2" value={vehicleType} onChange={e => setVehicleType(e.target.value)} placeholder="Vehicle Type" required />
    </div>
    <div className="col">
      <input className="form-control mb-2" value={fuelType} onChange={e => setFuelType(e.target.value)} placeholder="Fuel Type" required />
    </div>
    <div className="col">
      <input className="form-control mb-2" value={capacity} onChange={e => setCapacity(e.target.value)} placeholder="Capacity" required />
    </div>
    <div className="col">
      <input className="form-control mb-2" value={pricePerDay} onChange={e => setpricePerDay(e.target.value)} placeholder="Price" required />
    </div>
  </div>
  <button type="submit" className="btn btn-primary">Add Car</button>
</form>

  );
};

export default AddCarForm;
