import React, {useState } from 'react';
import { suggestCars } from '../api';

const SuggestButton= ()=>{
  const [fuelType , setFuelType] = useState('');
  const [capacity,setCapacity] = useState('');
  const [cars,setCars] = useState('');
  const [suggestedCars,setSuggestedCars] = useState('');

  const fetchCars = async () => {
      const response = await suggestCars(fuelType,capacity);
      setCars(response.data);
    };
  
    const handleSuggest = async (e) => {
      e.preventDefault();
      await fetchCars();
      setSuggestedCars(cars);
    };
  


  return (
    <form onSubmit={handleSuggest}>
      <h2>Suggest Car</h2>
      <input value={fuelType} onChange={e => setFuelType(e.target.value)} placeholder="Fuel Type" required />
      <input value={capacity} onChange={e => setCapacity(e.target.value)} placeholder="Capacity" required />
      <button type="submit">suggest</button>

      <ul>
        {cars.map((car)=>(
          <li key={car.id}>
            {car.name}-{car.fuelType}-{car.capacity}
          </li>
        )
        )}
      </ul>
    </form>
  );


}
export default SuggestButton;
