import React, { useState } from 'react';
import { addCar } from '../api';

const AddCar = () => {
  const [carData, setCarData] = useState({
    brand: '',
    vehicleType: '',
    fuelType: '',
    capacity: '',
    pricePerDay: '',
  });

  const handleChange = e => {
    setCarData({...carData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      // Prepare the payload - convert capacity and pricePerDay to numbers
      const payload = {
        ...carData,
        capacity: Number(carData.capacity),
        pricePerDay: Number(carData.pricePerDay),
        status: 'available'  // or any default status your Car class requires
      };

      await addCar(payload);
      alert('Car added successfully');
      // reset form
      setCarData({
        brand: '',
        vehicleType: '',
        fuelType: '',
        capacity: '',
        pricePerDay: '',
      });
    } catch (err) {
      alert('Failed to add car: ' + (err.response?.data || err.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Brand:</label>
      <input name="brand" value={carData.brand} onChange={handleChange} required />

      <label>Vehicle Type:</label>
      <input name="vehicleType" value={carData.vehicleType} onChange={handleChange} required />

      <label>Fuel Type:</label>
      <input name="fuelType" value={carData.fuelType} onChange={handleChange} required />

      <label>Capacity:</label>
      <input type="number" name="capacity" value={carData.capacity} onChange={handleChange} required />

      <label>Price Per Day:</label>
      <input type="number" name="pricePerDay" value={carData.pricePerDay} onChange={handleChange} required />

      <button type="submit">Add Car</button>
    </form>
  );
};

export default AddCar;
