import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RentCarForm from './RentCarForm';

const CarList = () => {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/cars')
      .then(response => setCars(response.data))
      .catch(error => console.error('Error fetching cars:', error));
  }, []);

  const handleRentClick = (car) => {
    setSelectedCar(car);
    const modal = new bootstrap.Modal(document.getElementById('rentCarModal'));
    modal.show();
  };

  const handleCloseRentForm = () => {
    setSelectedCar(null);
    const modal = bootstrap.Modal.getInstance(document.getElementById('rentCarModal'));
    modal.hide();
  };

  return (
    <div className="container mt-4">
  <h3 className="mb-3">Available Cars</h3>
  <div className="row">
    {cars.length === 0 ? (
      <div className="col-12">
        <div className="alert alert-warning">No available cars at the moment.</div>
      </div>
    ) : (
      cars.map(car => (
        <div className="col-md-4 mb-3" key={car.id}>
          <div className="card h-100 shadow-sm">
            <div className="card-body">
              <h5 className="card-title">{car.brand}</h5>
              <p className="card-text">
                <strong>Type:</strong> {car.vehicleType}<br />
                <strong>Fuel:</strong> {car.fuelType}<br />
                <strong>Capacity:</strong> {car.capacity}<br />
                <strong>Price:</strong> â‚¹{car.pricePerDay}/day
              </p>
              <span className={`badge ${car.status === 'rented' ? 'bg-danger' : 'bg-success'}`}>
                {car.status === 'rented' ? 'Rented' : 'Available'}
              </span>
              {car.status !== 'rented' && (
                <button className="btn btn-primary mt-3" onClick={() => handleRentClick(car)}>
                  Rent Now
                </button>
              )}
            </div>
          </div>
        </div>
      ))
    )}
  </div>
</div>

  );
};

export default CarList;
