import React, { useState } from 'react';
import { rentCar } from '../api'; // your axios call

const RentCarForm = ({ carId, carDetails, onClose }) => {
  const [days, setDays] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!days || isNaN(days) || days <= 0) {
      alert("Please enter a valid number of days");
      return;
    }
    try {
      const res = await rentCar(carId, parseInt(days));
      setMessage(res.data); // show backend success message
    } catch (error) {
      setMessage(error.response?.data || "Error renting car");
    }
  };

  return (
    <div>
      <h3>Rent Car - {carDetails.brand} ({carDetails.vehicleType})</h3>
      <p>Fuel Type: {carDetails.fuelType}</p>
      <p>Capacity: {carDetails.capacity}</p>
      <p>Price Per Day: â‚¹{carDetails.pricePerDay}</p>

      <form onSubmit={handleSubmit}>
        <label>Number of days to rent:</label>
        <input
          type="number"
          value={days}
          onChange={(e) => setDays(e.target.value)}
          min="1"
          required
        />
        <button type="submit">Calculate & Rent</button>
      </form>

      {message && <p>{message}</p>}
      <button onClick={onClose}>Close</button>
    </div>
  );
};

export default RentCarForm;
