import React, { useEffect, useState } from 'react';
import { getCars, rentCar } from '../api';

const CarList = () => {
  const [cars, setCars] = useState([]);

  const fetchCars = async () => {
    const response = await getCars();
    setCars(response.data);
  };

  useEffect(() => {
    fetchCars();
  }, []);

  const handleRent = async (id) => {
    await rentCar(id);
    fetchCars();
  };

  return (
    <div>
      <h2>Available Cars</h2>
      <ul>
        {cars.map(car => (
          <li key={car.id}>
            {car.brand} - {car.engineType} - {car.rented ? "Rented" : "Available"}
            {!car.rented && <button onClick={() => handleRent(car.id)}>Rent</button>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CarList;
