import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RentCarForm from './RentCarForm';

const CarList = () => {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8080/api/cars')
      .then(response => setCars(response.data))
      .catch(error => console.error('Error fetching cars:', error));
  }, []);

  const handleRentClick = (car) => {
    setSelectedCar(car);
    const modal = new bootstrap.Modal(document.getElementById('rentCarModal'));
    modal.show();
  };

  const handleCloseRentForm = () => {
    setSelectedCar(null);
    const modal = bootstrap.Modal.getInstance(document.getElementById('rentCarModal'));
    modal.hide();
  };

  return (
    <div className="container mt-4">
      <h3 className="mb-3">Available Cars</h3>
      <div className="row">
        {cars.map(car => (
          <div className="col-md-4 mb-3" key={car.id}>
            <div className="card h-100">
              <div className="card-body">
                <h5 className="card-title">{car.brand}</h5>
                <p className="card-text">
                  Type: {car.vehicleType}<br />
                  Fuel: {car.fuelType}<br />
                  Capacity: {car.capacity}<br />
                  â‚¹{car.pricePerDay}/day
                </p>
                <span className={`badge ${car.status === "rented" ? "bg-danger" : "bg-success"}`}>
                  {car.status === "rented" ? "Rented" : "Available"}
                </span>
              </div>
              <div className="card-footer bg-transparent">
                {car.status !== 'rented' && (
                  <button className="btn btn-primary btn-sm" onClick={() => handleRentClick(car)}>
                    Rent
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      <div className="modal fade" id="rentCarModal" tabIndex="-1" aria-labelledby="rentCarModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="rentCarModalLabel">Rent Car</h5>
              <button type="button" className="btn-close" onClick={handleCloseRentForm}></button>
            </div>
            <div className="modal-body">
              {selectedCar && (
                <RentCarForm carId={selectedCar.id} carDetails={selectedCar} onClose={handleCloseRentForm} />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CarList;
