import React, { useState } from 'react';
import { rentCar } from '../api';

const RentCarForm = ({ carId, carDetails, onClose }) => {
  const [days, setDays] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!days || isNaN(days) || days <= 0) {
      alert("Please enter a valid number of days");
      return;
    }
    setLoading(true);
    setMessage('');
    try {
      const res = await rentCar(carId, parseInt(days));
      setMessage(res.data); // backend success message
    } catch (error) {
      setMessage(error.response?.data || "Error renting car");
    }
    setLoading(false);
  };

  return (
    <div>
      <h3>Rent Car - {carDetails.brand} ({carDetails.vehicleType})</h3>
      <p><strong>Fuel Type:</strong> {carDetails.fuelType}</p>
      <p><strong>Capacity:</strong> {carDetails.capacity}</p>
      <p><strong>Price Per Day:</strong> â‚¹{carDetails.pricePerDay}</p>

      <form onSubmit={handleSubmit}>
        <label>Number of days to rent:</label>
        <input
          type="number"
          value={days}
          onChange={(e) => setDays(e.target.value)}
          min="1"
          required
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : "Calculate & Rent"}
        </button>
      </form>

      {message && <p style={{ marginTop: '10px', fontWeight: 'bold' }}>{message}</p>}
      <button onClick={onClose} style={{ marginTop: '10px' }}>Close</button>
    </div>
  );
};

export default RentCarForm;
