import React, { useEffect, useState } from 'react';
import { getCars } from '../api';
import RentCarForm from './RentCarForm';

const CarList = () => {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);

  const fetchCars = async () => {
    try {
      const response = await getCars();
      setCars(response.data);
    } catch (error) {
      alert("Failed to load cars: " + (error.response?.data || error.message));
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);

  const handleRentClick = (car) => {
    setSelectedCar(car);
  };

  const handleCloseRentForm = () => {
    setSelectedCar(null);
    fetchCars();  // refresh cars after rent or cancel
  };

  return (
    <div>
      <h2>Available Cars</h2>
      <ul>
        {cars.map(car => (
          <li key={car.id}>
            {car.brand} - {car.vehicleType} - {car.status === 'rented' ? "Rented" : "Available"}
            {car.status !== 'rented' && (
              <button style={{ marginLeft: '10px' }} onClick={() => handleRentClick(car)}>Rent</button>
            )}
          </li>
        ))}
      </ul>

      {selectedCar && (
        <div style={{ border: '1px solid #aaa', padding: '1rem', marginTop: '1rem', maxWidth: '400px', backgroundColor: '#f9f9f9' }}>
          {!car.rented && <RentCarForm carId={car.id} carDetails={car} onClose={fetchCars} />}
        </div>
      )}
    </div>
  );
};

export default CarList;
