import React, { useState } from 'react';
import { addCar } from '../api';

const AddCarForm = ({ onCarAdded }) => {
  const [brand, setBrand] = useState('');
  const [vehicleType, setVehicleType] = useState('');
  const [capacity, setCapacity] = useState('');
  const [fuelType, setFuelType] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addCar(brand,vehicleType, fuelType,capacity);
    setBrand('');
    setVehicleType('');
    setFuelType('');
    setCapacity('');
    onCarAdded();
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add New Car</h2>
      <input value={brand} onChange={e => setBrand(e.target.value)} placeholder="Brand" required />
      <input value={vehicleType} onChange={e => setVehicleType(e.target.value)} placeholder="Vehicle Type" required />
      <input value={fuelType} onChange={e => setFuelType(e.target.value)} placeholder="Fuel Type" required />
      <input value={capacity} onChange={e => setCapacity(e.target.value)} placeholder="Capacity" required />
      <button type="submit">Add Car</button>
    </form>
  );
};

export default AddCarForm;
